import queryString from "query-string";
import React from "react";
import axios from "axios";
import '../'

class Detail extends React.Component{

    constructor(){
        super();
        this.state = {
            restaurant :[],
            resId : undefined
        }
    }

    componentDidMount(){
        const q = queryString.parse(window.location.search);
        const { restaurant } = q;
        //console.log(restaurant);

        axios({
            url: `http://localhost:5500/restaurant/${restaurant}`,
            method: 'get',
            headers: { 'Content-Type': 'application/JSON'}
        })
        .then( res => {
            this.setState({ restaurant: res.data.restaurants, resId:restaurant })
        })
        .catch((err => console.log(err)))

    }

    render(){

        const { restaurant }= this.state;
        return(
            <div>
                  {/* <!--Navbar--> */}

                  <nav class="navbar bg-danger" data-bs-theme="">
                    <div class="container">
                        <div class="navbar-brand text-danger circle">
                            <h2 class="logo">e!</h2>
                        </div>
                        <form class="d-flex nav-form">
                            <button type="button" class="btn btn-danger">Login</button>
                            <button type="button" class="btn btn-outline-light">Create an account</button>
                        </form>
                    </div>
                </nav>
                
                <div className="bannerCover">
                    <img src= {restaurant.thumb} alt="" className="banner"/>
                </div>
            </div>
        )
    }

}
export default Detail;
