import queryString from "query-string";
import React from "react";
import axios from "axios";


class Detail extends React.Component{

    constructor(){
        super();
        this.state = {
            restaurant :[],
            resId : undefined
        }
    }

    componentDidMount(){
        const q = queryString.parse(window.location.search);
        const { restaurant } = q;
        //console.log(restaurant);

        axios({
            url: `http://localhost:5500/restaurant/${restaurant}`,
            method: 'get',
            headers: { 'Content-Type': 'application/JSON'}
        })
        .then( res => {
            this.setState({ restaurant: res.data.restaurants, resId:restaurant })
        })
        .catch((err => console.log(err)))

    }

    render(){

        const { restaurant }= this.state;
        return(
            <div>
                <div class="bg-cover bg-image d-flex">
                    <div class="container mt-3">
                        <div class="row">
                            <div class="col text-end">
                                <button type="button" class="btn btn-outline-light">Login</button>
                                <button type="button" class="btn btn-outline-light">Create an account</button>
                            </div>
                        </div>
                        <div class="row mt-5">
                            <div class="col d-flex justify-content-center">
                                <div class="text-danger circle">
                                    <h2 class="logo">e!</h2>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
                
                <div>
                    
                </div>
             <h2>{restaurant.name}</h2>
            </div>
        )
    }

}
export default Detail;
